dataRows = db_query("SELECT d.id, o1.name as areaName, o.Name AS comm_name, CASE WHEN r.resource_type=0 THEN '房屋区域' WHEN r.resource_type=1 THEN '房屋楼栋' WHEN r.resource_type=2 THEN '房屋单元' WHEN r.resource_type=3 THEN '房屋' WHEN r.resource_type=4 THEN '车位区域' WHEN r.resource_type=5 THEN '车位' END AS resource_type, r.resource_code, r.resource_name, r.calc_area, CASE WHEN r.resource_status=1 THEN '自持' WHEN r.resource_status=2 THEN '待售' WHEN r.resource_status=3 THEN '已售未收' WHEN r.resource_status=4 THEN '已收' WHEN r.resource_status=5 THEN '已收未装' WHEN r.resource_status=6 THEN '已收装修' WHEN r.resource_status=7 THEN '已装未住' WHEN r.resource_status=8 THEN '入住' END AS resource_status, cc.name, d.corp_cost_id, c.cost_name AS cost_name, cc.mobile, REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(c.business_type,'7','报事业务'),'6','装修业务'),'5','多经业务'),'4','租赁业务'),'3','能耗业务'),'2','车位业务'),'1','基础业务') AS busi_type, d.stan_id, st.stan_price, f.latefee_rate, d.fee_date, d.fee_due_date, d.fee_start_date, d.fee_end_date, f.calc_amount1, f.calc_amount2, d.tax_rate, f.due_amount, ABS(d.deal_amount) AS deal_amount, d.deal_date, b.bill_sign, CASE WHEN d.is_delete = 1 THEN '直接撤销' WHEN d.is_delete = 0 AND d.deal_type = '减免红冲' THEN '红冲撤销' ELSE '' END AS cancel_type FROM tb_charge_receipts_detail d LEFT JOIN erp_base.rf_organize o ON d.comm_id = o.Id AND o.OrganType = 6 AND o.Is_Delete = 0 AND o.Status = 0 LEFT JOIN erp_base.rf_organize o1 ON o.ParentId = o1.Id AND o1.Is_Delete = 0 AND o1.Status = 0 LEFT JOIN erp_base.tb_base_masterdata_resource r ON d.resource_id = r.id LEFT JOIN erp_base.tb_base_charge_cost c ON d.corp_cost_id = c.id AND c.is_delete = 0 LEFT JOIN erp_base.tb_base_charge_comm_stan st ON d.stan_id = st.id AND st.is_delete = 0 LEFT JOIN tidb_sync_combine.tb_charge_fee f ON d.fee_id = f.id AND f.is_delete = 0 LEFT JOIN tidb_sync_combine.tb_base_charge_bill_use b ON d.pay_flow_id = b.pay_flow_id AND b.is_delete = 0 LEFT JOIN erp_base.tb_base_masterdata_customer_comm cc ON d.customer_id = cc.id AND cc.is_delete = 0 WHERE 1=1 AND ((d.deal_type = '减免红冲' AND d.is_delete = 0) OR (d.deal_type = '减免' AND d.is_delete = 1)) ORDER BY d.is_delete DESC LIMIT 10 OFFSET 0 ")
set_result(rows=dataRows, message="ok")